import React, { useEffect, useState } from "react";
import API from "../services/api";

export default function ProductList() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    API.get("/products")
      .then((res) => {
        setProducts(res.data);
      })
      .catch(() => setError("Failed to load products"))
      .finally(() => setLoading(false));
  }, []);

  if (loading)
    return <p className="text-center mt-8 text-gray-700">Loading products...</p>;

  if (error)
    return (
      <p className="text-center mt-8 text-red-600 font-semibold">{error}</p>
    );

  if (!products.length)
    return <p className="text-center mt-8 text-gray-700">No products found.</p>;

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 p-4">
      {products.map((p) => (
        <div
          key={p._id}
          className="border rounded shadow p-4 hover:shadow-lg transition"
        >
          <h3 className="font-semibold text-lg mb-2">{p.name}</h3>
          <p>Quantity: {p.quantity}</p>
        </div>
      ))}
    </div>
  );
}
