import React, { useState } from "react";
import API from "../services/api";

export default function ProductForm({ onAdd }) {
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const submitHandler = async (e) => {
    e.preventDefault();
    setError(null);

    if (!name.trim() || !quantity) {
      setError("Please fill in all fields");
      return;
    }

    setLoading(true);

    try {
      await API.post("/products", {
        name: name.trim(),
        quantity: Number(quantity),
      });
      setName("");
      setQuantity("");
      onAdd();
    } catch (err) {
      setError(err.response?.data?.message || "Failed to add product");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form
      onSubmit={submitHandler}
      className="max-w-md mx-auto p-6 bg-white rounded shadow mb-8"
    >
      <h2 className="text-xl font-semibold mb-4 text-center">Add Product</h2>
      {error && (
        <p className="text-center mb-4 text-red-600 font-semibold">{error}</p>
      )}

      <input
        type="text"
        placeholder="Product Name"
        className="w-full border rounded px-4 py-2 mb-4 focus:outline-blue-500"
        value={name}
        onChange={(e) => setName(e.target.value)}
        disabled={loading}
        required
      />

      <input
        type="number"
        min="0"
        placeholder="Quantity"
        className="w-full border rounded px-4 py-2 mb-6 focus:outline-blue-500"
        value={quantity}
        onChange={(e) => setQuantity(e.target.value)}
        disabled={loading}
        required
      />

      <button
        type="submit"
        disabled={loading}
        className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-2 rounded disabled:opacity-50"
      >
        {loading ? "Adding..." : "Add Product"}
      </button>
    </form>
  );
}
